import React, { useState, useRef, useEffect } from "react";
import classes from "./SignupPage.module.css";
import LoginSignupLower from "../../containers/LoginSignupLower/LoginSignupLower";
// import { useSpring, animated, config } from "react-spring";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { setSignupPage } from "../../features/signupPageSlice";
import SignupFirst from "../../containers/SignupFirst/SignupFirst";

const SignupPage = (props) => {
  const { signupPage } = useSelector((state) => state);

  const dispatch = useDispatch(setSignupPage);

  const [email, setEmail] = useState("");
  const [emptyEmail, setEmptyEmail] = useState(false);
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [emptyConfirmPassword, setEmptyConfirmPassword] = useState(false);
  const [emptyPassword, setEmptyPassword] = useState(false);

  const loginBtnRef = useRef();
  const emailFieldRef = useRef();
  const passwordFieldRef = useRef();
  const confirmPasswordFieldRef = useRef();

  useEffect(() => {
    if (email.length !== 0) {
      setEmptyEmail(false);
    }
  }, [email]);

  useEffect(() => {
    if (password.length !== 0) {
      setEmptyPassword(false);
    }
  }, [password]);

  useEffect(() => {
    if (confirmPassword.length !== 0) {
      setEmptyConfirmPassword(false);
    }
  }, [confirmPassword]);

  const showPasswordHandler = (type) => {
    if (type === "password") {
      if (showPassword) {
        passwordFieldRef.current.type = "password";
        setShowPassword(false);
      } else {
        passwordFieldRef.current.type = "text";
        setShowPassword(true);
      }
    } else if (type === "confirmPassword") {
      if (showConfirmPassword) {
        confirmPasswordFieldRef.current.type = "password";
        setShowConfirmPassword(false);
      } else {
        confirmPasswordFieldRef.current.type = "text";
        setShowConfirmPassword(true);
      }
    }
  };

  const inputFocusHandler = (type) => {
    document.getElementById(type).addEventListener("keyup", (e) => {
      if (e.key === "Enter") {
        e.preventDefault();
        loginBtnRef.current.click();
      }
    });
  };

  const submitHandler = () => {
    if (signupPage === 1) {
    }

    dispatch(setSignupPage(signupPage + 1));
  };

  const backHandler = () => {
    dispatch(setSignupPage(signupPage - 1));
  };

  return (
    <div className={classes.SignupPage}>
      <Link to="/" className={classes.backToHome}>
        <i className="fa fa-chevron-left"></i>
        <p>Back home</p>
      </Link>
      <div className={classes.loginContainer}>
        <div className={classes.leftSide}>
          <SignupFirst />
        </div>
        <div className={classes.rightSide}>
          <img src="/images/bg.jpg" alt="img" />
          <div className={classes.overlay}></div>
        </div>
      </div>
    </div>
  );
};

export default SignupPage;
