import React, { useState, useRef, useEffect } from "react";
import LSPageTemp from "../../containers/LSPageTemp/LSPageTemp";
import classes from "./ForgetPasswordPage.module.css";

const ForgetPasswordPage = (props) => {
  const [email, setEmail] = useState("");
  const [emptyEmail, setEmptyEmail] = useState(false);

  const loginBtnRef = useRef();
  const emailFieldRef = useRef();

  useEffect(() => {
    if (email.length !== 0) {
      setEmptyEmail(false);
    }
  }, [email]);

  const inputFocusHandler = () => {
    document.getElementById("email").addEventListener("keyup", (e) => {
      if (e.key === "Enter") {
        e.preventDefault();
        loginBtnRef.current.click();
      }
    });
  };

  const submitHandler = () => {
    if (email.length === 0) {
      setEmptyEmail(true);
    } else {
    }
  };

  return (
    <LSPageTemp fixedLogo>
      <>
        <p>Please enter your registered email and we will send you an email to reset your password</p>
        <input
          className={[classes.emailField, emptyEmail ? classes.emptyEmail : null].join(" ")}
          type="email"
          id="email"
          ref={emailFieldRef}
          value={email.length === 0 ? "" : email}
          onFocus={inputFocusHandler}
          placeholder="Enter your email"
          onChange={(text) => setEmail(text.target.value)}
        />
        <div className={classes.submitButton} ref={loginBtnRef} onClick={submitHandler}>
          Submit
        </div>
      </>
    </LSPageTemp>
  );
};

export default ForgetPasswordPage;
