import { createSlice } from "@reduxjs/toolkit";

let todoId = 1;

export const slice = createSlice({
  name: "todo",
  initialState: [],
  reducers: {
    create: (state, action) => {
      state.push({
        id: todoId,
        description: action.payload,
        isComplete: false,
      });

      todoId++;
    },
    edit: (state, action) => {
      const todoToEdit = state.find((todo) => todo.id === action.payload.id);
      if (todoToEdit) {
        todoToEdit.description = action.payload.description;
      }
    },
    toggleComplete: (state, action) => {
      const todoToToggle = state.find((todo) => todo.id === action.payload.id);
      if (todoToToggle) {
        todoToToggle.isComplete = !todoToToggle.isComplete;
      }
    },
    remove: (state, action) => {
      const deleteIndex = state.findIndex((todo) => todo.id === action.payload.id);
      if (deleteIndex !== -1) {
        state.splice(deleteIndex, 1);
      }
    },
  },
});

export const { create, edit, toggleComplete, remove } = slice.actions;

export default slice.reducer;
