import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";

import { create, edit, toggleComplete, remove } from "./todoSlice";

const Todo = () => {
  const todo = useSelector((state) => state.todo);
  const [inputText, setInputText] = useState("");
  const dispatch = useDispatch(todo);

  const handleSubmit = (e) => {
    e.preventDefault();

    dispatch(create(inputText));

    setInputText("");
  };

  const handleDelete = (todoItem) => () => {
    dispatch(remove(todoItem));
  };

  const handleEdit = (todoItemId) => () => {
    dispatch(edit({
      id: todoItemId,
      description: inputText
    }));
    setInputText("");
  };

  const handleToggle = (todoItem) => () => {
    dispatch(toggleComplete(todoItem));
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input value={inputText} onChange={(e) => setInputText(e.target.value)} />
        <button type="submit">Create Todo</button>
      </form>
      {todo.map((todoItem) => (
        <div key={todoItem.id}>
          {todoItem.description} {todoItem.isComplete ? "(DONE)" : ""}
          <button onClick={handleDelete(todoItem)}>Delete</button>
          <button onClick={handleEdit(todoItem.id)}>Edit</button>
          <button onClick={handleToggle(todoItem)}>Toggle Complete</button>
        </div>
      ))}
    </div>
  );
};

export default Todo;
