import React, { useState, useRef, useEffect } from "react";
import FGButton from "../../components/FGButton/FGButton";
import classes from "./LoginPage.module.css";
import { Link } from "react-router-dom";

const LoginPage = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    console.log(email);
  }, [email]);

  const loginBtnRef = useRef();

  const inputHandler = (text, type) => {
    if (type === "email") {
      setEmail(text.target.value);
    }
  };

  const inputFocusHandler = (type) => {
    // setLoginClicked(false);
    document.getElementById(type).addEventListener("keyup", (e) => {
      if (e.keyCode === 13) {
        e.preventDefault();
        loginBtnRef.current.click();
      }
    });
  };

  const submitHandler = () => {
    alert("Logged in!");
  };

  return (
    <div className={classes.LoginPage}>
      <div className={classes.loginContainer}>
        <div className={classes.rightSide}>
          <img src="/images/bg.jpg" alt="img" />
          <div className={classes.overlay}></div>
        </div>
        <div className={classes.leftSide}>
          <div className={classes.leftSideContainer}>
            <div className={classes.inputsContainer}>
              <input
                type="text"
                id="email"
                // value={email.length === 0 ? "" : email}
                onFocus={() => inputFocusHandler("email")}
                placeholder="Enter your email"
                onChange={(text) => inputHandler(text, "email")}
              />
              <p className={classes.submitButton} ref={loginBtnRef} onClick={submitHandler}>
                Log in
              </p>
            </div>
            <p className={classes.or}>or</p>
            <div className={classes.socialMediaButtons}>
              <FGButton type="facebook" login />
              <FGButton type="google" login />
            </div>
            <div className={classes.noAccount}>
              <h4>
                Not a member yet?{" "}
                <Link to="/signup" className={classes.signupButton}>
                  Sign up now
                </Link>
              </h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
